import React, { useState } from 'react';
import { createProfile } from '../api';

function CreateProfileForm() {
  const [formData, setFormData] = useState({
    type: 'Personal',
    name: '',
    email: '',
    address: '',
    contact: '',
    website: '',
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await createProfile(formData);
      if (response.message === 'Profile created successfully') {
        // Clear the form fields or reset the form state if needed
        setFormData({
          type: 'Personal',
          name: '',
          email: '',
          address: '',
          contact: '',
          website: '',
        });

        // Display a success message
        setSuccessMessage('Profile created successfully');
        // Clear any previous error messages
        setErrorMessage('');
      } else {
        // Display an error message
        setErrorMessage('Profile creation failed');
        // Clear any previous success messages
        setSuccessMessage('');
      }
    } catch (error) {
      console.error('Error creating profile', error);
      // Display an error message
      setErrorMessage('Profile creation failed');
      // Clear any previous success messages
      setSuccessMessage('');
    }
  };


  return (
    <div className="container">
      <h2>Create Profile</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Type:</label>
          <select className="form-control" name="type" value={formData.type} onChange={handleChange}>
            <option value="Personal">Personal</option>
            <option value="Organization">Organization</option>
          </select>
        </div>
        <div className="form-group">
          <label>Name:</label>
          <input type="text" className="form-control" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Address:</label>
          <input type="text" className="form-control" name="address" value={formData.address} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Contact:</label>
          <input type="text" className="form-control" name="contact" value={formData.contact} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Website:</label>
          <input type="url" className="form-control" name="website" value={formData.website} onChange={handleChange} />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">Create Profile</button>
        </div>
      </form>
    </div>
  );
}

export default CreateProfileForm;
