import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // If you use React Router for routing
import { readQR } from '../api';

function ViewQRCode() {
  const { id } = useParams(); // If you use React Router, this will extract the profile ID from the URL

  const [qrCodeData, setQRCodeData] = useState(null);

  useEffect(() => {
    const fetchQRCode = async () => {
      try {
        const response = await readQR(id); // Use the readQR API function to fetch QR code data by ID
        if (response.qr) {
          setQRCodeData(response.qr.data);
        } else {
          console.error('QR code not found');
        }
      } catch (error) {
        console.error('Error fetching QR code', error);
      }
    };

    fetchQRCode();
  }, [id]);

  return (
    <div>
      <h2>View QR Code</h2>
      {qrCodeData ? (
        <div>
          <p>Scannable QR Code:</p>
          <img src={`data:image/png;base64,${qrCodeData}`} alt="QR Code" />
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default ViewQRCode;
