import React, { useState } from "react";
import { createQR } from "../api"; // Import your API function for creating QR codes
import QRCode from "qrcode.react"; // Import QRCode component

function CreateQRCode() {
  const [qrData, setQRData] = useState({
    type: "",
    name: "",
    email: "",
    address: "",
    contact: "",
    website: "",
  });

  const [generatedQRCode, setGeneratedQRCode] = useState(null); // State to store generated QR code data

  const handleChange = (e) => {
    const { name, value } = e.target;
    setQRData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleCreateQRCode = async () => {
    try {
      // Call the API function to create a QR code
      const response = await createQR(qrData);
      alert("QR code created successfully");
      // Set the generated QR code data
      setGeneratedQRCode(response.qrCodeData); // Assuming your API returns the QR code data
    } catch (error) {
      console.error("Error creating QR code:", error);
    }
  };

  return (
    <div>
      <h2>Create QR Code</h2>
      <div>
        <div>
          <label>Type:</label>
          <input
            type="text"
            name="type"
            value={qrData.type}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={qrData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={qrData.email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Address:</label>
          <input
          className='form-control'
            type="text"
            name="address"
            value={qrData.address}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Contact:</label>
          <input
                    className='form-control'
            type="text"
            name="contact"
            value={qrData.contact}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Website:</label>
          <input
                    className='form-control'

            type="text"
            name="website"
            value={qrData.website}
            onChange={handleChange}
          />
        </div>
      </div>

      <button onClick={handleCreateQRCode}>Create QR Code</button>

      {generatedQRCode && (
        <div>
          <h3>Generated QR Code:</h3>
          <QRCode value={generatedQRCode} size={128} />
        </div>
      )}
    </div>
  );
}

export default CreateQRCode;
