import React, { useState } from 'react';
import { createQR } from '../api'; // Import your API function for creating QR codes

function CreateQRCode() {
  const [qrData, setQRData] = useState({
    type: '',
    name: '',
    email: '',
    address: '',
    contact: '',
    website: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setQRData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleCreateQRCode = async () => {
    try {
      // Call the API function to create a QR code
      const response = await createQR(qrData);
      alert('QR code created successfully');
      // Optionally, you can display or save the created QR code data
      console.log('Created QR Code:', response);
    } catch (error) {
      console.error('Error creating QR code:', error);
    }
  };

  return (
    <div>
      <h2>Create QR Code</h2>
      <div>
        <label>Type:</label>
        <input type="text" name="type" value={qrData.type} onChange={handleChange} />
      </div>
      <div>
        <label>Name:</label>
        <input type="text" name="name" value={qrData.name} onChange={handleChange} />
      </div>
      <div>
        <label>Email:</label>
        <input type="text" name="email" value={qrData.email} onChange={handleChange} />
      </div>
      <div>
        <label>Address:</label>
        <input type="text" name="address" value={qrData.address} onChange={handleChange} />
      </div>
      <div>
        <label>Contact:</label>
        <input type="text" name="contact" value={qrData.contact} onChange={handleChange} />
      </div>
      <div>
        <label>Website:</label>
        <input type="text" name="website" value={qrData.website} onChange={handleChange} />
      </div>
      <button onClick={handleCreateQRCode}>Create QR Code</button>
    </div>
  );
}

export default CreateQRCode;
