import React, { useState } from 'react';
import QRCode from 'qrcode.react'; // Import QRCode component
import { createQR } from './api'; // Import your API function

function CreateQR() {
  const [qrData, setQRData] = useState({
    name: '',
    contact: '',
    website: '',
  });
  const [qrCodeValue, setQRCodeValue] = useState('');

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setQRData({
      ...qrData,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Call your API function to create the QR code
      const response = await createQR(qrData);
      alert('QR code generated successfully');
      // Set the generated QR code value
      setQRCodeValue(response.qrCode); // Assuming the API response contains the QR code value
    } catch (error) {
      console.error('Error generating QR code:', error);
    }
  };

  return (
    <div>
      <h2>Create QR Code</h2>
      <form onSubmit={handleSubmit}>
      <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={qrData.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Contact:</label>
          <input
            type="text"
            name="contact"
            value={qrData.contact}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Website:</label>
          <input
            type="text"
            name="website"
            value={qrData.website}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <button type="submit">Generate QR Code</button>
        </div>
      </form>
      {qrCodeValue && (
        <div>
          <h3>Generated QR Code:</h3>
          <QRCode value={qrCodeValue} />
        </div>
      )}
    </div>
  );
}

export default CreateQR;
