import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom'; // If you use React Router for routing
import { readProfile, updateProfile } from '../api';

function UpdateProfile() {
  const { id } = useParams(); // If you use React Router, this will extract the profile ID from the URL
  const history = useHistory(); // Use history to navigate back to the profile view after updating

  const [formData, setFormData] = useState({
    type: '',
    name: '',
    email: '',
    address: '',
    contact: '',
    website: '',
  });

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await readProfile(id); // Use the readProfile API function to fetch profile data by ID
        if (response.profile) {
          setFormData(response.profile);
        } else {
          console.error('Profile not found');
        }
      } catch (error) {
        console.error('Error fetching profile', error);
      }
    };

    fetchProfile();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await updateProfile(id, formData); // Use the updateProfile API function to update the profile
      if (response.message === 'Profile updated successfully') {
        // Redirect to the profile view page or display a success message
        // You can implement this based on your app's navigation logic.
        // For now, you can just console.log(response) to see the API response.
        console.log(response);

        // Navigate back to the profile view page
        history.push(`/view/${id}`);
      } else {
        console.error('Profile update failed');
      }
    } catch (error) {
      console.error('Error updating profile', error);
    }
  };

  return (
    <div>
      <h2>Update Profile</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Type:</label>
          <select name="type" value={formData.type} onChange={handleChange}>
            <option value="Personal">Personal</option>
            <option value="Organization">Organization</option>
          </select>
        </div>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" name="address" value={formData.address} onChange={handleChange} />
        </div>
        <div>
          <label>Contact:</label>
          <input type="text" name="contact" value={formData.contact} onChange={handleChange} />
        </div>
        <div>
          <label>Website:</label>
          <input type="url" name="website" value={formData.website} onChange={handleChange} />
        </div>
        <div>
          <button type="submit">Update Profile</button>
        </div>
      </form>
    </div>
  );
}

export default UpdateProfile;
