import React, { useState } from 'react';
import QRCode from 'qrcode.react'; // Import a QR code generation library
import { createQR } from '../api'; // Import your API function

function CreateQR() {
  const [qrData, setQRData] = useState({
    type: '',
    name: '',
    email: '',
    address: '',
    contact: '',
    website: '',
  });

  const [generatedQR, setGeneratedQR] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setQRData({
      ...qrData,
      [name]: value,
    });
  };

  const generateQRCode = async () => {
    try {
      // Call your API function to create the QR code
      const result = await createQR(qrData);
      setGeneratedQR(result.qrData); // Assuming your API response contains the generated QR code data
    } catch (error) {
      console.error('Error creating QR code:', error);
    }
  };

  return (
    <div>
      <h2>Create QR Code</h2>
      <form>
        <div>
          <label>Type:</label>
          <input
            type="text"
            name="type"
            value={qrData.type}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={qrData.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="text"
            name="email"
            value={qrData.email}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Address:</label>
          <input
            type="text"
            name="address"
            value={qrData.address}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Contact:</label>
          <input
            type="text"
            name="contact"
            value={qrData.contact}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Website:</label>
          <input
            type="text"
            name="website"
            value={qrData.website}
            onChange={handleInputChange}
          />
        </div>
        <button type="button" onClick={generateQRCode}>
          Generate QR Code
        </button>
      </form>

      {generatedQR && (
        <div>
          <h3>Generated QR Code</h3>
          <QRCode value={generatedQR} />
        </div>
      )}
    </div>
  );
}

export default CreateQR;
