import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom
import { createProfile } from '../api'; // Import your API function

function CreateProfile() {
  const history = useHistory(); // Initialize history
  const [profileData, setProfileData] = useState({
    // Initialize state for profile data
    type: '',
    name: '',
    email: '',
    address: '',
    contact: '',
    website: '',
  });

  const handleCreateProfile = async () => {
    try {
      // Call your API function to create the profile
      await createProfile(profileData);
      // After successful creation, display a success message
      alert('Profile created successfully');
      // Redirect to the ViewProfile page
      history.push(`/view-profile`);
    } catch (error) {
      console.error('Error creating profile:', error);
    }
  };

  // Rest of your CreateProfile component code
  return (
    <div className="container">
      <h2>Create Profile</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Type:</label>
          <input type="text" className="form-control" name="type" value={formData.type} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>Name:</label>
          <input type="text" className="form-control" name="name" value={formData.name} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>Address:</label>
          <input type="text" className="form-control" name="address" value={formData.address} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>Contact:</label>
          <input type="text" className="form-control" name="contact" value={formData.contact} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>Website:</label>
          <input type="url" className="form-control" name="website" value={formData.website} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">Create Profile</button>
        </div>
      </form>
    </div>
  );
}

export default CreateProfileForm;
