import React, { useState } from 'react';
import { useParams } from 'react-router-dom'; // Assuming you are using React Router

import { readProfile } from '../api'; // Import your API function

function ViewProfile() {
  const { id } = useParams(); // Get the profile ID from the route parameter

  const [profile, setProfile] = useState(null);
  const [showProfile, setShowProfile] = useState(false);

  const handleViewProfileClick = async () => {
    try {
      const result = await readProfile(id); // Call your API function to read the profile by ID
      setProfile(result);
      setShowProfile(true);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  return (
    <div>
      <h2>This is the View Pofile Page!</h2>

      <button onClick={handleViewProfileClick} className="btn btn-primary">View Profile</button>
      {showProfile && (
        <div>
          <h2>Profile Details</h2>
          <p>Type: {profile.type}</p>
          <p>Name: {profile.name}</p>
          <p>Email: {profile.email}</p>
          <p>Address: {profile.address}</p>
          <p>Contact: {profile.contact}</p>
          <p>Website: {profile.website}</p>
          <button onClick={handleViewProfileClick}>View Profile</button>
        </div>

      )}
    </div>
  );
}

export default ViewProfile;
