import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom'; // If you use React Router for routing
import { readProfile } from '../api';

function ViewProfile() {
  const { id } = useParams(); // If you use React Router, this will extract the profile ID from the URL

  const [profile, setProfile] = useState(null);
  const [showProfile, setShowProfile] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await readProfile(id); // Use the readProfile API function to fetch profile data by ID
        if (response.profile) {
          setProfile(response.profile);
        } else {
          console.error('Profile not found');
        }
      } catch (error) {
        console.error('Error fetching profile', error);
      }
    };

    fetchProfile();
  }, [id]);

  const handleViewProfile = () => {
    setShowProfile(true);
  };

  return (
    <div>
      <h2>View Profile</h2>
      {showProfile ? (
        <div>
          <p>Type: {profile?.type}</p>
          <p>Name: {profile?.name}</p>
          <p>Email: {profile?.email}</p>
          <p>Address: {profile?.address}</p>
          <p>Contact: {profile?.contact}</p>
          <p>Website: {profile?.website}</p>
        </div>
      ) : (
        <div>
          <button onClick={handleViewProfile} className="btn btn-primary">View Profile</button>
        </div>
      )}
       <Link to={`/viewqr/${id}`}>
            <button className="btn btn-primary">View QR Code</button>
          </Link>
    </div>
  );
}

export default ViewProfile;
